oracle.codeExample:
// Fetch external data with secret injection
const result = await hub.oracle.fetch({
  url: 'https://api.example.com/data',
  headers: { 'Authorization': 'Bearer {{API_KEY}}' },
  secretRefs: ['API_KEY']
});
console.log('Data:', result.data);

vrf.codeExample:
// Request verifiable random number
const { randomValue, proof } = await hub.vrf.random({
  seed: 'unique-request-id',
  min: 1,
  max: 100
});
// Verify result locally or on-chain
const isValid = await hub.vrf.verify(randomValue, proof);

secrets.codeExample:
// Store a secret
await hub.secrets.set('MY_API_KEY', 'sk-xxx...', {
  description: 'OpenAI API Key',
  allowedApps: ['my-app-id']
});

// Use in Oracle request (auto-injected)
const data = await hub.oracle.fetch({
  url: 'https://api.openai.com/v1/chat',
  secretRefs: ['MY_API_KEY']
});

datafeeds.codeExample:
// Get latest price
const price = await hub.datafeeds.getPrice('BTC-USD');
console.log(`BTC: $${price.value} at ${price.timestamp}`);

// Subscribe to updates
hub.datafeeds.subscribe('ETH-USD', (update) => {
  console.log(`ETH: $${update.value}`);
});

automation.codeExample:
// Create a daily task
const task = await hub.automation.create({
  name: 'Daily Report',
  trigger: { type: 'cron', schedule: '0 9 * * *' },
  action: { type: 'webhook', url: 'https://my-app.com/report' }
});

gasbank.codeExample:
// Check balance
const balance = await hub.gasbank.getBalance();
console.log(`Available: ${balance.available} GAS`);

// Deposit GAS
const deposit = await hub.gasbank.deposit({
  amount: 100,
  txHash: '0x...' 
});

mixer.codeExample:
// Deposit to mixer pool
const note = await hub.mixer.deposit({ amount: 10, pool: 'GAS-10' });

// Withdraw later with note
await hub.mixer.withdraw({ note, recipient: 'NXxx...' });

ccip.codeExample:
